/*
|--------------------------------------------------------------------------
| Panel
|--------------------------------------------------------------------------
*/

jQuery(document).ready(function() { 
  jQuery(".checkboxes").iphoneStyle({ resizeContainer: false, checkedLabel: "AKTİF", uncheckedLabel: "PASİF" }); 
  jQuery(".tooltips").tooltip();
  jQuery(".dateformat").mask("99.99.9999"); 
  jQuery(".phoneformat").mask("(0999)9999999");   
  jQuery(".moneyformat").maskMoney();  
  jQuery(".editor").summernote({ 
      toolbar: [
        ['style', ['style']],
        ['font', ['bold', 'italic', 'underline', 'clear']],
        ['fontname', ['fontname']],
        ['fontsize', ['fontsize']],
        ['color', ['color']],
        ['para', ['ul', 'ol', 'paragraph']],
        ['height', ['height']],
        ['table', ['table']],
        ['insert', ['link', 'picture', 'hr']],
        ['view', ['fullscreen', 'codeview']]
      ],
  lang:'tr-TR', height: 300 });
});

/*
|--------------------------------------------------------------------------
| Left Side Bar
|--------------------------------------------------------------------------
*/

jQuery(".left-sidebar-toggle").click(function() { $(".leftmenu").slideToggle("slow"); });

/*
|--------------------------------------------------------------------------
| Left Search Box
|--------------------------------------------------------------------------
*/

jQuery(".search").keyup(function(){      
  var search = jQuery(this).val().toLowerCase();        
  jQuery(".leftmenu li").each(function(){
    var li = jQuery(this).text().toLowerCase();
    open = li.indexOf(search) !== -1;
    jQuery(this).toggle(open);
  });     
});